const AvailableMeals = () => {
  const [meals, setMeals] = useSt
   useEffect(()=>{
    const fetchMeal = async ()=>{

      const responce = await fetch('https://react-http-ba874-default-rtdb.firebaseio.com/meals.json');
      const responceData = await responce.json();

      const loadedMeals= [];
      for(const key in responceData){
        loadedMeals.push({
          id:key,
          name:responceData[key].name,
          description:responceData[key].description,
          price:responceData[key].price
        });
      }

    }


    fetchMeal();
   },[])



  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
