import { Modal } from "bootstrap";
import Login from "./Login";

const LoginBtn =()=>{
    const [isLogin , setIsLogin] =useSt(true);

    const LoginHandler =()=>{
        setIsLogin(true);
    }

    return(
        <Modal>
             {isLogin &&<Login />}
            <button type="login-link" onClick={LoginHandler} >sign in</button>
        </Modal>
    );
}

export default LoginBtn;
